import React, { useState } from 'react';

const TaskForm = ({ addTask, categories, addCategory }) => {
  const [taskName, setTaskName] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [dueDate, setDueDate] = useState('');
  const [category, setCategory] = useState(categories[0] || '');
  const [isAddingNew, setIsAddingNew] = useState(false);
  const [newCategory, setNewCategory] = useState(''); 

  const handleSubmit = (e) => {
    e.preventDefault();
    const newTask = {
      id: Date.now(),
      name: taskName,
      priority,
      dueDate,
      category,
    };
    addTask(newTask);
    setTaskName('');
    setPriority('Medium');
    setDueDate('');
    setCategory(categories[0] || ''); // Reset category
  };

  const handleCategoryChange = (e) => {
    const selected = e.target.value;
    if (selected === 'new') {
      setIsAddingNew(true);
    } else {
      setCategory(selected);
      setIsAddingNew(false);
    }
  };

  const handleNewCategoryChange = (e) => {
    setNewCategory(e.target.value);
  };

  const handleAddNewCategory = (e) => {
    if (e.key === 'Enter' && newCategory.trim() !== '') {
      if (!categories.includes(newCategory.trim())) {
        addCategory(newCategory.trim());
        setCategory(newCategory.trim());
        setNewCategory('');
        setIsAddingNew(false);
      }
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="mb-4 bg-white p-6 rounded shadow-lg">
      <div className="mb-4">
        <input
          type="text"
          placeholder="Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
      </div>

      <div className="mb-4">
        {!isAddingNew ? (
          <select
            value={category}
            onChange={handleCategoryChange}
            className="w-full p-2 border rounded"
          >
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
            <option value="new">+ New</option>
          </select>
        ) : (
          <input
            type="text"
            placeholder="Enter new category"
            value={newCategory}
            onChange={handleNewCategoryChange}
            onKeyDown={handleAddNewCategory}
            className="w-full p-2 border rounded"
            autoFocus
          />
        )}
      </div>

      <div className="mb-4">
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          className="w-full p-2 border rounded"
          
        />
      </div>

      <div className="mb-4">
        <select
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
          className="w-full p-2 border rounded"
        >
          <option value="Urgent">Urgent ðŸ”´</option>
          <option value="High">High ðŸŸ¡</option>
          <option value="Low">Low ðŸŸ¢</option>
        </select>
      </div>
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
        Add Task
      </button>
    </form>
  );
};

export default TaskForm;
