import React from 'react';

const TodoItem = ({ task, deleteTask }) => {
  return (
    <div className="p-4 mb-4 bg-white rounded shadow-lg flex justify-between items-center">
      <div>
        <h2 className="text-xl font-semibold">{task.name}</h2>
        <p className="text-gray-500">{task.category}</p>
        <p className="text-sm text-gray-400">Due: {task.dueDate}</p>
      </div>
      <button
        className="bg-red-500 text-white px-4 py-2 rounded"
        onClick={() => deleteTask(task.id)}
      >
        Delete
      </button>
    </div>
  );
};

export default TodoItem;
